#!/bin/bash
# Quick setup script for .env file

if [ -f .env ]; then
    echo ".env file already exists. Skipping creation."
    exit 0
fi

cat > .env << 'ENVEOF'
# AI Evolution Platform - Environment Configuration
# Generated by setup_env.sh

# ML Infra Service Configuration
CHAT_BASE_URL=http://localhost:8000
CHAT_PLATFORM_AUTH_TOKEN=your-auth-token-here
ACCOUNT_ID=default
ORG_ID=default
PROJECT_ID=default

# Database Configuration
POSTGRES_USER=ai_evolution
POSTGRES_PASSWORD=ai_evolution_dev
POSTGRES_DB=ai_evolution
POSTGRES_HOST=localhost
POSTGRES_PORT=5432
DATABASE_URL=postgresql+asyncpg://${POSTGRES_USER}:${POSTGRES_PASSWORD}@${POSTGRES_HOST}:${POSTGRES_PORT}/${POSTGRES_DB}

# Server Configuration
DEBUG=true
RELOAD=true
HOST=0.0.0.0
PORT=7890

# Optional: Langfuse
LANGFUSE_PUBLIC_KEY=
LANGFUSE_SECRET_KEY=
LANGFUSE_HOST=http://localhost:3000

# Optional: LLM API Keys
OPENAI_API_KEY=
ANTHROPIC_API_KEY=
ENVEOF

echo ".env file created successfully!"
echo "Please update CHAT_BASE_URL and CHAT_PLATFORM_AUTH_TOKEN with your actual values."
